import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import '../styles/ProductPage.css';

const ProductPage = () => {
  const [product, setProduct] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [quantity, setQuantity] = useState(1);
  const { id } = useParams();
  const navigate = useNavigate();
  const { isAuthenticated } = useAuth();

  useEffect(() => {
    const fetchProduct = async () => {
      try {
        setLoading(true);
        
        const response = await fetch(`/api/products/${id}`);
        if (!response.ok) throw new Error('Network response was not ok');
        const data = await response.json();
        setProduct(data);
        setLoading(false);
      } catch (err) {
        setError('Failed to load product details');
        setLoading(false);
      }
    };

    fetchProduct();
  }, [id]);

  const handleQuantityChange = (e) => {
    const value = parseInt(e.target.value);
    if (value > 0 && value <= (product?.stock || 1)) {
      setQuantity(value);
    }
  };

  const addToCart = async () => {
    if (!isAuthenticated) {
      navigate('/login', { state: { from: `/products/${id}` } });
      return;
    }

    try {
      // In a real app, this would be an API call to add to cart
      console.log(`Adding ${quantity} of product ${id} to cart`);
      
      // Navigate to cart
      navigate('/cart');
    } catch (err) {
      setError('Failed to add product to cart');
    }
  };

  if (loading) {
    return (
      <div className="product-loading">
        <div className="loading-spinner"></div>
        <p>Loading product details...</p>
      </div>
    );
  }

  if (error) {
    return (
      <div className="product-error">
        <h2>Error</h2>
        <p>{error}</p>
        <button onClick={() => navigate('/')} className="return-button">
          Return to Home
        </button>
      </div>
    );
  }

  if (!product) {
    return (
      <div className="product-not-found">
        <h2>Product Not Found</h2>
        <p>The product you're looking for doesn't exist or has been removed.</p>
        <button onClick={() => navigate('/')} className="return-button">
          Return to Home
        </button>
      </div>
    );
  }

  const formattedDate = new Date(product.createdAt).toLocaleDateString();

  return (
    <div className="product-page">
      <div className="product-container">
        <div className="product-gallery">
          <img 
            src={product.imageUrl} 
            alt={product.name} 
            className="product-main-image" 
          />
          <div className="product-thumbnails">
            {product.images && product.images.map((image, index) => (
              <img 
                key={index}
                src={image}
                alt={`${product.name} view ${index + 1}`}
                className="product-thumbnail"
              />
            ))}
          </div>
        </div>
        
        <div className="product-details">
          <h1 className="product-name">{product.name}</h1>
          
          <div className="product-price">${product.price.toFixed(2)}</div>
          
          <div className="product-meta">
            <span className="product-condition">Condition: {product.condition}</span>
            <span className="product-category">Category: {product.category}</span>
            <span className="product-seller">Seller: {product.seller.name}</span>
            <span className="product-listing-date">Listed on: {formattedDate}</span>
          </div>
          
          <div className="product-description">
            <h3>Description</h3>
            <p>{product.description}</p>
          </div>
          
          {product.features && product.features.length > 0 && (
            <div className="product-features">
              <h3>Features</h3>
              <ul>
                {product.features.map((feature, index) => (
                  <li key={index}>{feature}</li>
                ))}
              </ul>
            </div>
          )}
          
          <div className="product-actions">
            <div className="quantity-selector">
              <label htmlFor="quantity">Quantity:</label>
              <input
                type="number"
                id="quantity"
                min="1"
                max={product.stock}
                value={quantity}
                onChange={handleQuantityChange}
              />
              <span className="stock-info">
                {product.stock > 0 
                  ? `${product.stock} available` 
                  : 'Out of stock'}
              </span>
            </div>
            
            <button 
              onClick={addToCart}
              className="add-to-cart-button"
              disabled={product.stock < 1}
            >
              Add to Cart
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ProductPage;
