import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import axios from 'axios';
import { API_URL } from '../config';
import '../styles/BecomeSeller.css';

const BecomeSeller = () => {
  const { currentUser, loadUser } = useAuth();
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState(false);
  const navigate = useNavigate();

  // Add debugging for the current user state
  useEffect(() => {
    console.log("BecomeSeller - Current user:", currentUser);
  }, [currentUser]);

  useEffect(() => {
    // If user is already a seller, redirect to sell page
    if (currentUser && currentUser.role === 'seller') {
      navigate('/sell');
    }
  }, [currentUser, navigate]);

  const handleBecomeSeller = async () => {
    try {
      setLoading(true);
      setError('');
      
      console.log("Attempting to become a seller...");
      
      // Make sure we have a token
      const token = localStorage.getItem('token');
      if (!token) {
        throw new Error('You must be logged in to become a seller');
      }

      console.log("Token found, making API request...");

      // Call the API to update user role
      const response = await axios.put(
        `${API_URL}/api/user/become-seller`,
        {},
        {
          headers: {
            Authorization: `Bearer ${token}`
          }
        }
      );

      console.log("API response:", response.data);

      if (response.data.success) {
        setSuccess(true);
        // Reload user data to update role in context
        await loadUser();
        
        console.log("User role updated successfully!");
        
        // Redirect to sell page after 2 seconds
        setTimeout(() => {
          navigate('/sell');
        }, 2000);
      } else {
        throw new Error(response.data.message || 'Failed to become a seller');
      }
    } catch (error) {
      console.error('Error becoming seller:', error);
      setError(error.response?.data?.message || error.message || 'Failed to become a seller');
    } finally {
      setLoading(false);
    }
  };

  // If no current user or still loading auth state, show login prompt
  if (!currentUser) {
    return (
      <div className="become-seller-page">
        <div className="become-seller-container">
          <h2>Become a Seller</h2>
          <p>Please log in to continue</p>
          <button 
            onClick={() => navigate('/login', { state: { from: '/become-seller' } })}
            className="login-button"
          >
            Log In
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="become-seller-page">
      <div className="become-seller-container">
        <h2>Become a Seller</h2>
        <p>Join our marketplace and start selling your products today!</p>
        
        {error && <div className="error-message">{error}</div>}
        {success && (
          <div className="success-message">
            Your account has been upgraded to seller status! Redirecting you to the seller dashboard...
          </div>
        )}
        
        {!success && (
          <div className="benefits">
            <h3>Benefits of becoming a seller:</h3>
            <ul>
              <li>List your products to thousands of potential customers</li>
              <li>Easy-to-use seller dashboard</li>
              <li>Secure payment processing</li>
              <li>Dedicated seller support</li>
            </ul>
          </div>
        )}
        
        {!success && (
          <button 
            onClick={handleBecomeSeller} 
            disabled={loading || success}
            className="become-seller-button"
          >
            {loading ? 'Processing...' : 'Become a Seller'}
          </button>
        )}
      </div>
    </div>
  );
};

export default BecomeSeller;
