import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { cartApi, orderApi } from '../services/api';
import { useAuth } from '../contexts/AuthContext';

function CheckoutPage() {
  const navigate = useNavigate();
  const { currentUser } = useAuth();
  
  const [cart, setCart] = useState({ items: [] });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [isProcessing, setIsProcessing] = useState(false);
  const [orderPlaced, setOrderPlaced] = useState(false);

  useEffect(() => {
    fetchCart();
  }, []);

  const fetchCart = async () => {
    try {
      setLoading(true);
      const response = await cartApi.get();
      setCart(response.data.cart);
      setLoading(false);
    } catch (error) {
      console.error('Error fetching cart:', error);
      setError('Failed to load your cart items');
      setLoading(false);
    }
  };

  const calculateTotal = () => {
    return cart.items.reduce((total, item) => {
      return total + (item.product.price * item.quantity);
    }, 0).toFixed(2);
  };

  const handlePlaceOrder = async () => {
    if (cart.items.length === 0) {
      setError('Your cart is empty');
      return;
    }

    try {
      setIsProcessing(true);
      await orderApi.checkout();
      setOrderPlaced(true);
      setIsProcessing(false);
    } catch (error) {
      console.error('Error placing order:', error);
      setError('Failed to place your order. Please try again.');
      setIsProcessing(false);
    }
  };

  if (loading) {
    return (
      <div className="flex justify-center items-center h-64">
        <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-primary"></div>
      </div>
    );
  }

  if (orderPlaced) {
    return (
      <div className="container mx-auto px-4 py-8">
        <div className="max-w-2xl mx-auto bg-white rounded-lg shadow-md p-8 text-center">
          <div className="w-16 h-16 bg-green-100 text-green-500 rounded-full flex items-center justify-center mx-auto mb-6">
            <svg className="h-8 w-8" fill="none" viewBox="0 0 24 24" stroke="currentColor">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M5 13l4 4L19 7" />
            </svg>
          </div>
          <h2 className="text-2xl font-bold mb-4">Order Placed Successfully!</h2>
          <p className="mb-6 text-gray-600">Thank you for your purchase. You will receive a confirmation email shortly.</p>
          <div className="flex flex-col sm:flex-row gap-4 justify-center">
            <button 
              onClick={() => navigate('/orders')}
              className="px-6 py-3 bg-primary text-white font-medium rounded-md hover:bg-opacity-90 transition"
            >
              View Orders
            </button>
            <button 
              onClick={() => navigate('/products')}
              className="px-6 py-3 border border-gray-300 text-gray-700 font-medium rounded-md hover:bg-gray-50 transition"
            >
              Continue Shopping
            </button>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="container mx-auto px-4 py-8">
      <h1 className="text-3xl font-bold mb-8">Checkout</h1>
      
      {error && (
        <div className="bg-red-100 border-l-4 border-red-500 text-red-700 p-4 rounded mb-6">
          <p>{error}</p>
        </div>
      )}
      
      {cart.items.length === 0 ? (
        <div className="text-center py-12 bg-white rounded-lg shadow-md">
          <h2 className="text-2xl font-semibold mb-4">Your cart is empty</h2>
          <p className="mb-6 text-gray-600">Add some items to your cart before proceeding to checkout.</p>
          <button 
            onClick={() => navigate('/products')}
            className="px-6 py-3 bg-primary text-white font-medium rounded-md hover:bg-opacity-90 transition"
          >
            Browse Products
          </button>
        </div>
      ) : (
        <div className="flex flex-col lg:flex-row gap-8">
          {/* Order Summary */}
          <div className="lg:w-2/3">
            <div className="bg-white rounded-lg shadow-md p-6">
              <h2 className="text-lg font-semibold mb-4">Order Summary</h2>
              
              <div className="overflow-x-auto">
                <table className="min-w-full divide-y divide-gray-200">
                  <thead className="bg-gray-50">
                    <tr>
                      <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                        Product
                      </th>
                      <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                        Price
                      </th>
                      <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                        Quantity
                      </th>
                      <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                        Total
                      </th>
                    </tr>
                  </thead>
                  <tbody className="bg-white divide-y divide-gray-200">
                    {cart.items.map((item) => (
                      <tr key={item.product._id}>
                        <td className="px-6 py-4">
                          <div className="flex items-center">
                            <div className="h-12 w-12 flex-shrink-0 rounded overflow-hidden">
                              <img 
                                src={item.product.images[0]} 
                                alt={item.product.title}
                                className="h-full w-full object-cover"
                              />
                            </div>
                            <div className="ml-4">
                              <div className="text-sm font-medium text-gray-900">{item.product.title}</div>
                              <div className="text-sm text-gray-500 capitalize">{item.product.category}</div>
                            </div>
                          </div>
                        </td>
                        <td className="px-6 py-4">
                          <div className="text-sm text-gray-900">${item.product.price}</div>
                        </td>
                        <td className="px-6 py-4">
                          <div className="text-sm text-gray-900">{item.quantity}</div>
                        </td>
                        <td className="px-6 py-4">
                          <div className="text-sm font-medium text-gray-900">
                            ${(item.product.price * item.quantity).toFixed(2)}
                          </div>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            </div>
            
            {/* Customer Info */}
            <div className="bg-white rounded-lg shadow-md p-6 mt-6">
              <h2 className="text-lg font-semibold mb-4">Customer Information</h2>
              
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <div>
                  <h3 className="font-medium mb-2">Personal Details</h3>
                  <div className="bg-gray-50 p-3 rounded">
                    <p><span className="font-medium">Name:</span> {currentUser?.name}</p>
                    <p><span className="font-medium">Email:</span> {currentUser?.email}</p>
                  </div>
                </div>
                
                <div>
                  <h3 className="font-medium mb-2">Shipping Information</h3>
                  <div className="bg-gray-50 p-3 rounded text-sm text-gray-600">
                    <p>To reduce our environmental footprint, all transactions are handled digitally. Once your order is confirmed, you'll receive instructions on how to collect your items.</p>
                  </div>
                </div>
              </div>
            </div>
          </div>
          
          {/* Payment Summary */}
          <div className="lg:w-1/3">
            <div className="bg-white rounded-lg shadow-md p-6 sticky top-20">
              <h2 className="text-lg font-semibold mb-4">Payment Summary</h2>
              
              <div className="space-y-4">
                <div className="flex justify-between pb-4">
                  <span>Subtotal</span>
                  <span>${calculateTotal()}</span>
                </div>
                
                <div className="flex justify-between pb-4 border-b">
                  <span>Shipping Fee</span>
                  <span>$0.00</span>
                </div>
                
                <div className="flex justify-between font-semibold text-lg">
                  <span>Total</span>
                  <span className="text-primary">${calculateTotal()}</span>
                </div>
                
                <button
                  onClick={handlePlaceOrder}
                  disabled={isProcessing || cart.items.length === 0}
                  className={`w-full py-3 bg-primary text-white font-medium rounded-md hover:bg-opacity-90 transition mt-4 ${
                    (isProcessing || cart.items.length === 0) ? 'opacity-70 cursor-not-allowed' : ''
                  }`}
                >
                  {isProcessing ? 'Processing...' : 'Place Order'}
                </button>
                
                <div className="text-center mt-4">
                  <button 
                    onClick={() => navigate('/cart')}
                    className="text-sm text-primary hover:underline"
                  >
                    Back to Cart
                  </button>
                </div>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default CheckoutPage;
