import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import '../styles/HomePage.css';

const HomePage = () => {
  const [featuredProducts, setFeaturedProducts] = useState([]);
  const [categories, setCategories] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // In a real app, this would be API calls
    // For demo purposes, using mock data
    const mockProducts = [
      {
        _id: '1',
        name: 'Vintage Leather Jacket',
        price: 89.99,
        category: 'Clothing',
        condition: 'Good',
        imageUrl: 'https://via.placeholder.com/300x300?text=Leather+Jacket'
      },
      {
        _id: '2',
        name: 'Retro Record Player',
        price: 149.99,
        category: 'Electronics',
        condition: 'Excellent',
        imageUrl: 'https://via.placeholder.com/300x300?text=Record+Player'
      },
      {
        _id: '3',
        name: 'Antique Wooden Chair',
        price: 59.99,
        category: 'Furniture',
        condition: 'Fair',
        imageUrl: 'https://via.placeholder.com/300x300?text=Wooden+Chair'
      },
      {
        _id: '4',
        name: 'Vintage Camera',
        price: 79.99,
        category: 'Electronics',
        condition: 'Good',
        imageUrl: 'https://via.placeholder.com/300x300?text=Vintage+Camera'
      },
      {
        _id: '5',
        name: 'Mid-Century Desk Lamp',
        price: 49.99,
        category: 'Home Decor',
        condition: 'Excellent',
        imageUrl: 'https://via.placeholder.com/300x300?text=Desk+Lamp'
      },
      {
        _id: '6',
        name: 'Denim Jacket',
        price: 39.99,
        category: 'Clothing',
        condition: 'Like New',
        imageUrl: 'https://via.placeholder.com/300x300?text=Denim+Jacket'
      }
    ];

    const mockCategories = [
      { id: 1, name: 'Clothing', count: 42, icon: 'ðŸ‘•' },
      { id: 2, name: 'Furniture', count: 28, icon: 'ðŸª‘' },
      { id: 3, name: 'Electronics', count: 36, icon: 'ðŸ“±' },
      { id: 4, name: 'Books', count: 54, icon: 'ðŸ“š' },
      { id: 5, name: 'Home Decor', count: 31, icon: 'ðŸ ' },
      { id: 6, name: 'Jewelry', count: 19, icon: 'ðŸ’' }
    ];

    // Simulate API delay
    setTimeout(() => {
      setFeaturedProducts(mockProducts);
      setCategories(mockCategories);
      setLoading(false);
    }, 500);
  }, []);

  return (
    <div className="home-page">
      {/* Hero Section */}
      <section className="hero-section">
        <div className="hero-content">
          <h1>Find Unique Treasures at Amazing Prices</h1>
          <p>Browse our curated collection of quality second-hand items</p>
          <Link to="/products" className="browse-button">Browse Products</Link>
        </div>
      </section>

      {/* Categories Section */}
      <section className="categories-section">
        <div className="section-header">
          <h2>Shop by Category</h2>
          <Link to="/products" className="view-all">View All</Link>
        </div>

        {loading ? (
          <div className="loading-spinner"></div>
        ) : (
          <div className="categories-grid">
            {categories.map(category => (
              <Link 
                to={`/products?category=${category.name}`} 
                className="category-card" 
                key={category.id}
              >
                <div className="category-icon">{category.icon}</div>
                <h3>{category.name}</h3>
                <p>{category.count} items</p>
              </Link>
            ))}
          </div>
        )}
      </section>

      {/* Featured Products Section */}
      <section className="featured-products-section">
        <div className="section-header">
          <h2>Featured Products</h2>
          <Link to="/products" className="view-all">View All</Link>
        </div>

        {loading ? (
          <div className="loading-spinner"></div>
        ) : (
          <div className="products-grid">
            {featuredProducts.map(product => (
              <Link to={`/products/${product._id}`} className="product-card" key={product._id}>
                <div className="product-image">
                  <img src={product.imageUrl} alt={product.name} />
                </div>
                <div className="product-info">
                  <h3 className="product-name">{product.name}</h3>
                  <p className="product-price">${product.price.toFixed(2)}</p>
                  <div className="product-meta">
                    <span className="product-category">{product.category}</span>
                    <span className="product-condition">{product.condition}</span>
                  </div>
                </div>
              </Link>
            ))}
          </div>
        )}
      </section>

      {/* Sell With Us Section */}
      <section className="sell-with-us-section">
        <div className="sell-content">
          <h2>Have Items to Sell?</h2>
          <p>Join our community of sellers and turn your unused items into cash</p>
          <div className="benefits">
            <div className="benefit">
              <span className="benefit-icon">ðŸ’°</span>
              <h3>Make Money</h3>
              <p>Earn cash from items you no longer need</p>
            </div>
            <div className="benefit">
              <span className="benefit-icon">ðŸŒ±</span>
              <h3>Eco-Friendly</h3>
              <p>Give pre-loved items a second life</p>
            </div>
            <div className="benefit">
              <span className="benefit-icon">ðŸ›’</span>
              <h3>Easy Process</h3>
              <p>Simple listing and shipping process</p>
            </div>
          </div>
          <Link to="/register" className="become-seller-button">Become a Seller</Link>
        </div>
      </section>
    </div>
  );
};

export default HomePage;
