import React, { useEffect, useState } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import { API_URL } from '../config';
import { FcGoogle } from 'react-icons/fc';

const LoginPage = () => {
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const { currentUser, handleLogin } = useAuth();
  const navigate = useNavigate();
  const location = useLocation();

  useEffect(() => {
    // Check if user is already logged in
    if (currentUser) {
      const redirectTo = location.state?.from || '/';
      navigate(redirectTo, { replace: true });
      return;
    }

    const handleGoogleCallback = async () => {
      const params = new URLSearchParams(location.search);
      const token = params.get('token');

      if (token) {
        try {
          setLoading(true);
          setError('');
          
          console.log('Received token from Google OAuth');
          
          // Store token and update user state
          await handleLogin(token);
          
          console.log('Login successful, redirecting to home');
          
          // Navigate to home page after successful login
          navigate('/', { replace: true });
        } catch (error) {
          console.error('Login error:', error);
          setError('Failed to complete login. Please try again.');
        } finally {
          setLoading(false);
        }
      }
    };

    handleGoogleCallback();
  }, [location, handleLogin, navigate, currentUser]);

  const handleGoogleLogin = () => {
    console.log('Initiating Google login...');
    window.location.href = `${API_URL}/auth/google`;
  };

  return (
    <div className="min-h-[calc(100vh-4rem)] flex items-center justify-center bg-gray-50 py-12 px-4 sm:px-6 lg:px-8">
      <div className="max-w-md w-full bg-white shadow-lg rounded-lg p-8">
        <div className="text-center">
          <h2 className="text-3xl font-bold text-gray-900 mb-8">
            Welcome Back
          </h2>
          <p className="text-gray-600 mb-8">
            Sign in to access your account
          </p>
        </div>

        {error && (
          <div className="mb-4 p-4 rounded-md bg-red-50 border border-red-200">
            <p className="text-sm text-red-600">{error}</p>
          </div>
        )}

        {loading ? (
          <div className="flex justify-center items-center py-4">
            <div className="loading-spinner"></div>
          </div>
        ) : (
          <button
            onClick={handleGoogleLogin}
            disabled={loading}
            className="w-full flex items-center justify-center gap-3 px-4 py-3 border border-gray-300 rounded-md shadow-sm bg-white text-gray-700 hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500 transition-colors duration-200"
          >
            <FcGoogle className="w-5 h-5" />
            <span className="text-sm font-medium">
              Continue with Google
            </span>
          </button>
        )}

        <div className="mt-6 text-center">
          <p className="text-sm text-gray-600">
            Don't have an account?{' '}
            <button
              onClick={() => navigate('/register')}
              className="font-medium text-blue-600 hover:text-blue-500"
            >
              Sign up
            </button>
          </p>
        </div>
      </div>
    </div>
  );
};

export default LoginPage;
