import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { API_URL } from '../config';
import axios from 'axios';
import '../styles/HomePage.css';

const HomePage = () => {
  const [featuredProducts, setFeaturedProducts] = useState([]);
  const [categories, setCategories] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        setLoading(true);
        const response = await axios.get(`${API_URL}/api/products`);
        setFeaturedProducts(response.data.products);
        setError(null);
      } catch (err) {
        console.error('Error fetching products:', err);
        setError('Failed to load products');
      } finally {
        setLoading(false);
      }
    };

    fetchProducts();
  }, []);

  return (
    <div className="home-page">
      {/* Hero Section */}
      <section className="hero-section">
        <div className="hero-content">
          <h1>Find Unique Treasures at Amazing Prices</h1>
          <p>Browse our curated collection of quality second-hand items</p>
          <Link to="/products" className="browse-button">Browse Products</Link>
        </div>
      </section>

      {/* Featured Products Section */}
      <section className="featured-products-section">
        <div className="section-header">
          <h2>Featured Products</h2>
          <Link to="/products" className="view-all">View All</Link>
        </div>

        {loading ? (
          <div className="loading-spinner"></div>
        ) : error ? (
          <div className="error-message">{error}</div>
        ) : (
          <div className="products-grid">
            {featuredProducts.map(product => (
              <Link to={`/products/${product._id}`} className="product-card" key={product._id}>
                <div className="product-image">
                  <img src={product.images[0] || 'https://via.placeholder.com/300x300?text=No+Image'} alt={product.name} />
                </div>
                <div className="product-info">
                  <h3 className="product-name">{product.name}</h3>
                  <p className="product-price">${product.price.toFixed(2)}</p>
                  <div className="product-meta">
                    <span className="product-category">{product.category}</span>
                    <span className="product-condition">{product.condition}</span>
                  </div>
                </div>
              </Link>
            ))}
          </div>
        )}
      </section>

      {/* Sell With Us Section */}
      <section className="sell-with-us-section">
        <div className="sell-content">
          <h2>Want to Sell Your Items?</h2>
          <p>Join our community of sellers and start making money from your unused items</p>
          <Link to="/become-seller" className="sell-button">Become a Seller</Link>
        </div>
      </section>
    </div>
  );
};

export default HomePage;
