import React, { useState, useEffect } from 'react';
import { useParams, Link, useNavigate } from 'react-router-dom';
import { productApi } from '../services/api';
import { useAuth } from '../contexts/AuthContext';
import { cartApi } from '../services/api';

function ProductDetailPage() {
  const { id } = useParams();
  const navigate = useNavigate();
  const { isAuthenticated } = useAuth();
  const [product, setProduct] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [quantity, setQuantity] = useState(1);
  const [adding, setAdding] = useState(false);
  const [relatedProducts, setRelatedProducts] = useState([]);

  useEffect(() => {
    fetchProduct();
  }, [id]);

  const fetchProduct = async () => {
    setLoading(true);
    setError(null);
    try {
      const response = await productApi.getProductById(id);
      setProduct(response.data);
      fetchRelatedProducts(response.data.category);
    } catch (err) {
      console.error('Error fetching product:', err);
      setError('Failed to load product. Please try again later.');
    } finally {
      setLoading(false);
    }
  };

  const fetchRelatedProducts = async (category) => {
    try {
      const response = await productApi.getProducts({ category, limit: 4 });
      // Filter out the current product
      const related = response.data.products.filter(item => item._id !== id);
      setRelatedProducts(related.slice(0, 3)); // Take maximum 3 products
    } catch (err) {
      console.error('Error fetching related products:', err);
      // Not setting error state as this is not critical
    }
  };

  const handleQuantityChange = (e) => {
    const value = parseInt(e.target.value);
    if (value > 0 && value <= product.stock) {
      setQuantity(value);
    }
  };

  const increaseQuantity = () => {
    if (quantity < product.stock) {
      setQuantity(quantity + 1);
    }
  };

  const decreaseQuantity = () => {
    if (quantity > 1) {
      setQuantity(quantity - 1);
    }
  };

  const handleAddToCart = async () => {
    if (!isAuthenticated) {
      navigate('/login', { state: { from: `/product/${id}` } });
      return;
    }

    setAdding(true);
    try {
      await cartApi.addToCart(id, quantity);
      // Show success message
      alert('Product added to cart successfully!');
    } catch (err) {
      console.error('Error adding to cart:', err);
      alert('Failed to add product to cart. Please try again.');
    } finally {
      setAdding(false);
    }
  };

  if (loading) {
    return (
      <div className="flex justify-center items-center h-64">
        <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-primary"></div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="container mx-auto px-4 py-8">
        <div className="bg-red-100 border-l-4 border-red-500 text-red-700 p-4 mb-4" role="alert">
          <p>{error}</p>
        </div>
        <Link to="/" className="text-primary hover:underline">
          Return to homepage
        </Link>
      </div>
    );
  }

  if (!product) {
    return (
      <div className="container mx-auto px-4 py-8">
        <div className="bg-yellow-100 border-l-4 border-yellow-500 text-yellow-700 p-4 mb-4" role="alert">
          <p>Product not found.</p>
        </div>
        <Link to="/" className="text-primary hover:underline">
          Return to homepage
        </Link>
      </div>
    );
  }

  return (
    <div className="container mx-auto px-4 py-8">
      {/* Breadcrumb */}
      <nav className="flex mb-8 text-sm">
        <Link to="/" className="text-gray-500 hover:text-primary">Home</Link>
        <span className="mx-2 text-gray-500">/</span>
        <Link to={`/category/${product.category}`} className="text-gray-500 hover:text-primary">
          {product.category}
        </Link>
        <span className="mx-2 text-gray-500">/</span>
        <span className="text-gray-900">{product.name}</span>
      </nav>

      <div className="lg:flex -mx-4">
        {/* Product Image */}
        <div className="lg:w-1/2 px-4 mb-8 lg:mb-0">
          <div className="bg-gray-100 rounded-lg overflow-hidden">
            <img 
              src={product.imageUrl || 'https://via.placeholder.com/500x500?text=No+Image'} 
              alt={product.name}
              className="w-full h-auto object-contain min-h-[400px]"
            />
          </div>
        </div>

        {/* Product Info */}
        <div className="lg:w-1/2 px-4">
          <h1 className="text-3xl font-bold mb-4">{product.name}</h1>
          
          <div className="flex items-center mb-6">
            <span className="text-2xl font-bold text-primary">${product.price.toFixed(2)}</span>
            {product.originalPrice && product.originalPrice > product.price && (
              <span className="ml-3 text-lg text-gray-500 line-through">
                ${product.originalPrice.toFixed(2)}
              </span>
            )}
          </div>
          
          <div className="mb-6 prose max-w-none">
            <p>{product.description}</p>
          </div>
          
          <div className="mb-6">
            <div className="flex items-center text-sm mb-2">
              <span className="mr-2 font-medium">Condition:</span>
              <span className="capitalize px-2 py-1 bg-gray-100 rounded-full">
                {product.condition || 'Used'}
              </span>
            </div>
            <div className="flex items-center text-sm mb-2">
              <span className="mr-2 font-medium">Category:</span>
              <Link to={`/category/${product.category}`} className="text-primary hover:underline">
                {product.category}
              </Link>
            </div>
            <div className="flex items-center text-sm">
              <span className="mr-2 font-medium">Availability:</span>
              {product.stock > 0 ? (
                <span className="text-green-600">
                  In Stock ({product.stock} {product.stock === 1 ? 'item' : 'items'} left)
                </span>
              ) : (
                <span className="text-red-600">Out of Stock</span>
              )}
            </div>
          </div>
          
          {product.stock > 0 && (
            <div className="mb-8">
              <div className="flex items-center mb-4">
                <span className="mr-4 font-medium">Quantity:</span>
                <div className="flex border border-gray-300 rounded-md">
                  <button 
                    onClick={decreaseQuantity}
                    className="px-3 py-1 border-r border-gray-300 focus:outline-none"
                    disabled={quantity <= 1}
                  >
                    âˆ’
                  </button>
                  <input
                    type="number"
                    value={quantity}
                    onChange={handleQuantityChange}
                    min="1"
                    max={product.stock}
                    className="w-16 text-center py-1 focus:outline-none"
                  />
                  <button 
                    onClick={increaseQuantity}
                    className="px-3 py-1 border-l border-gray-300 focus:outline-none"
                    disabled={quantity >= product.stock}
                  >
                    +
                  </button>
                </div>
              </div>
              
              <button
                onClick={handleAddToCart}
                disabled={adding}
                className="w-full md:w-auto bg-primary text-white py-3 px-8 rounded-md hover:bg-primary-dark focus:outline-none transition flex items-center justify-center"
              >
                {adding ? (
                  <>
                    <svg className="animate-spin -ml-1 mr-2 h-4 w-4 text-white" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24">
                      <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
                      <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
                    </svg>
                    Adding to Cart...
                  </>
                ) : (
                  'Add to Cart'
                )}
              </button>
            </div>
          )}
          
          {/* Seller Info */}
          <div className="border-t pt-6">
            <h3 className="text-lg font-semibold mb-3">Seller Information</h3>
            <div className="flex items-center">
              <div className="w-10 h-10 bg-gray-200 rounded-full flex items-center justify-center">
                <span className="font-medium text-gray-700">
                  {product.seller?.name ? product.seller.name.charAt(0).toUpperCase() : 'S'}
                </span>
              </div>
              <div className="ml-3">
                <div className="font-medium">{product.seller?.name || 'Anonymous Seller'}</div>
                <div className="text-sm text-gray-500">Member since {product.seller?.joinDate ? new Date(product.seller.joinDate).toLocaleDateString() : 'N/A'}</div>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Related Products */}
      {relatedProducts.length > 0 && (
        <div className="mt-16">
          <h2 className="text-2xl font-bold mb-8">You May Also Like</h2>
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
            {relatedProducts.map(product => (
              <div key={product._id} className="bg-white rounded-lg shadow-md overflow-hidden hover:shadow-lg transition">
                <Link to={`/product/${product._id}`}>
                  <div className="aspect-w-1 aspect-h-1 bg-gray-200">
                    <img
                      src={product.imageUrl || 'https://via.placeholder.com/300x300?text=No+Image'}
                      alt={product.name}
                      className="object-cover w-full h-48"
                    />
                  </div>
                  <div className="p-4">
                    <h3 className="text-lg font-semibold mb-2 hover:text-primary transition">{product.name}</h3>
                    <p className="text-primary font-bold">${product.price.toFixed(2)}</p>
                  </div>
                </Link>
              </div>
            ))}
          </div>
        </div>
      )}
    </div>
  );
}

export default ProductDetailPage;
