import React, { createContext, useState, useContext, useEffect } from 'react';
import * as authService from '../services/authService';

export const AuthContext = createContext();

export const useAuth = () => {
  return useContext(AuthContext);
};

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);

  // Load user from local storage on initial render
  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      handleLogin(token);
    } else {
      setLoading(false);
    }
  }, []);

  // Handle login
  const login = async (email, password) => {
    try {
      const response = await authService.login(email, password);
      localStorage.setItem('token', response.token);
      setUser(response.user);
      return response;
    } catch (error) {
      console.error('Login error:', error);
      throw error;
    }
  };

  // Handle logout
  const logout = () => {
    localStorage.removeItem('token');
    setUser(null);
  };

  // Handle token from OAuth
  const handleLogin = async (token) => {
    try {
      localStorage.setItem('token', token);
      authService.setAuthToken(token);
      
      // Fetch the user data with the token
      try {
        console.log('Fetching user data with token');
        const userData = await authService.getCurrentUser();
        console.log('User data received:', userData);
        if (userData && userData.user) {
          setUser(userData.user);
        } else {
          throw new Error('Invalid user data received');
        }
      } catch (fetchError) {
        console.error('Error fetching user data:', fetchError);
        // Fallback to decoded token if fetching fails
        try {
          // Get user info from token (simple JWT decode)
          const base64Url = token.split('.')[1];
          const base64 = base64Url.replace(/-/g, '+').replace(/_/g, '/');
          const tokenData = JSON.parse(window.atob(base64));
          
          setUser({
            _id: tokenData.userId || tokenData.id || '123',
            name: tokenData.name || 'User',
            email: tokenData.email || 'user@example.com',
            role: tokenData.role || 'user'
          });
        } catch (decodeError) {
          console.error('Token decode error:', decodeError);
          throw new Error('Failed to get user data');
        }
      }
      
      setLoading(false);
    } catch (error) {
      console.error('Authentication error:', error);
      localStorage.removeItem('token');
      setUser(null);
      setLoading(false);
    }
  };

  const value = {
    user,
    loading,
    login,
    logout,
    handleLogin,
    isAuthenticated: !!user
  };

  return (
    <AuthContext.Provider value={value}>
      {!loading && children}
    </AuthContext.Provider>
  );
};
