import React, { useState, useEffect } from 'react';
import { Link, useNavigate, useLocation } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import { API_URL } from '../config';
import '../styles/LoginPage.css';
import { FcGoogle } from 'react-icons/fc';
import axios from 'axios';

const LoginPage = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const [oauthStatus, setOauthStatus] = useState('');
  const { currentUser, handleLogin } = useAuth();
  const navigate = useNavigate();
  const location = useLocation();

  useEffect(() => {
    // Check if user is already logged in
    if (currentUser) {
      const redirectTo = location.state?.from || '/';
      navigate(redirectTo, { replace: true });
      return;
    }

    // Check for token in URL (for OAuth redirect)
    const searchParams = new URLSearchParams(location.search);
    const token = searchParams.get('token');
    
    if (token) {
      setOauthStatus('Processing Google login...');
      setLoading(true);
      
      // Store token and update user state
      handleLogin(token)
        .then(() => {
          setOauthStatus('Login successful! Redirecting...');
          const redirectTo = location.state?.from || '/';
          setTimeout(() => {
            navigate(redirectTo, { replace: true });
          }, 1000);
        })
        .catch((err) => {
          console.error('Login error:', err);
          setError('Failed to complete Google login');
          setOauthStatus('Login failed');
        })
        .finally(() => {
          setLoading(false);
        });
    } else if (searchParams.get('error')) {
      setOauthStatus('OAuth Error: Login failed');
      setError('Login failed');
    }
  }, [currentUser, navigate, location, handleLogin]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setLoading(true);

    try {
      const response = await axios.post(`${API_URL}/auth/login`, { email, password });
      
      if (response.data.success && response.data.token) {
        await handleLogin(response.data.token);
        const redirectTo = location.state?.from || '/';
        navigate(redirectTo, { replace: true });
      } else {
        throw new Error(response.data.message || 'Login failed');
      }
    } catch (err) {
      setError(err.response?.data?.message || err.message || 'Failed to login');
    } finally {
      setLoading(false);
    }
  };

  const handleGoogleLogin = () => {
    setOauthStatus('Redirecting to Google...');
    setLoading(true);
    // Use window.location.href instead of replace to allow the redirect back
    window.location.href = `${API_URL}/auth/google`;
  };

  return (
    <div className="login-container">
      <div className="login-card">
        <h1>Welcome Back</h1>
        <p className="subtitle">Sign in to your Thrift Store account</p>
        
        {error && <div className="error-message">{error}</div>}
        {oauthStatus && <div className="oauth-status">{oauthStatus}</div>}
        
        <form onSubmit={handleSubmit} className="login-form">
          <div className="form-group">
            <label htmlFor="email">Email</label>
            <input
              type="email"
              id="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
              disabled={loading}
            />
          </div>
          
          <div className="form-group">
            <label htmlFor="password">Password</label>
            <input
              type="password"
              id="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
              disabled={loading}
            />
          </div>
          
          <button type="submit" className="login-button" disabled={loading}>
            {loading ? 'Signing in...' : 'Sign In'}
          </button>
        </form>
        
        <div className="divider">or</div>
        
        <div className="social-login">
          <button 
            className="google-btn" 
            onClick={handleGoogleLogin}
            disabled={loading}
          >
            <FcGoogle size={24} />
            Continue with Google
          </button>
        </div>
        
        <p className="register-link">
          Don't have an account? <Link to="/register" className={loading ? 'disabled' : ''}>Sign up</Link>
        </p>
        
        <p className="terms">
          By signing in, you agree to our <Link to="/terms" className={loading ? 'disabled' : ''}>Terms of Service</Link> and <Link to="/privacy" className={loading ? 'disabled' : ''}>Privacy Policy</Link>
        </p>
      </div>
    </div>
  );
};

export default LoginPage;
