import React, { createContext, useState, useContext, useEffect } from 'react';
import { API_URL } from '../config';
import axios from 'axios';
import { jwtDecode } from 'jwt-decode';

const AuthContext = createContext();

// Helper function to decode JWT token
const decodeToken = (token) => {
  try {
    console.log('Decoding token...');
    const decoded = jwtDecode(token);
    console.log('Decoded token:', decoded);
    return decoded;
  } catch (error) {
    console.error('Error decoding token:', error);
    return null;
  }
};

export const AuthProvider = ({ children }) => {
  const [currentUser, setCurrentUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Function to load user data from token
  const loadUser = async () => {
    setLoading(true);
    setError(null);
    
    try {
      const token = localStorage.getItem('token');
      
      if (!token) {
        console.log('No token found in localStorage');
        setCurrentUser(null);
        setLoading(false);
        return;
      }
      
      // Try to decode token first
      const decodedToken = decodeToken(token);
      if (!decodedToken) {
        console.error('Invalid token format');
        localStorage.removeItem('token');
        setCurrentUser(null);
        setLoading(false);
        return;
      }
      
      console.log('Token expiry:', new Date(decodedToken.exp * 1000).toLocaleString());
      
      // Check if token is expired
      if (decodedToken.exp * 1000 < Date.now()) {
        console.log('Token expired');
        localStorage.removeItem('token');
        setCurrentUser(null);
        setLoading(false);
        return;
      }
      
      // Set basic user info from token
      const basicUser = {
        id: decodedToken.userId || decodedToken.id,
        name: decodedToken.name,
        email: decodedToken.email,
        role: decodedToken.role
      };
      
      console.log('Setting basic user info:', basicUser);
      setCurrentUser(basicUser);
      
      // Fetch complete user data from server
      console.log('Fetching complete user data from server...');
      const response = await axios.get(`${API_URL}/auth/me`, {
        headers: {
          Authorization: `Bearer ${token}`
        }
      });
      
      if (response.data.success) {
        console.log('User data fetched successfully:', response.data.user);
        setCurrentUser(response.data.user);
      } else {
        console.error('Error fetching user data');
        setError('Error fetching user data');
      }
    } catch (error) {
      console.error('Error loading user:', error);
      
      // If the error is due to authentication, clear the token
      if (error.response && (error.response.status === 401 || error.response.status === 403)) {
        console.log('Authentication error, clearing token');
        localStorage.removeItem('token');
        setCurrentUser(null);
      } else {
        // For other errors, keep the basic user info from the token
        setError('Error connecting to server');
      }
    } finally {
      setLoading(false);
    }
  };

  // Login function
  const handleLogin = async (token) => {
    console.log('Handling login with token...');
    try {
      // Store token first
      localStorage.setItem('token', token);
      
      // Decode token to get basic user info
      const decodedToken = decodeToken(token);
      if (!decodedToken) {
        throw new Error('Invalid token');
      }
      
      // Set basic user info immediately
      const basicUser = {
        id: decodedToken.userId || decodedToken.id,
        name: decodedToken.name,
        email: decodedToken.email,
        role: decodedToken.role
      };
      setCurrentUser(basicUser);
      
      // Then load complete user data
      await loadUser();
    } catch (error) {
      console.error('Error during login:', error);
      localStorage.removeItem('token');
      setCurrentUser(null);
      throw error;
    }
  };

  // Logout function
  const handleLogout = () => {
    console.log('Logging out...');
    localStorage.removeItem('token');
    setCurrentUser(null);
  };

  // Check for token on initial load
  useEffect(() => {
    console.log('AuthContext: Initial load');
    loadUser();
  }, []);

  return (
    <AuthContext.Provider
      value={{
        currentUser,
        loading,
        error,
        handleLogin,
        handleLogout,
        loadUser
      }}
    >
      {children}
    </AuthContext.Provider>
  );
};

export const useAuth = () => useContext(AuthContext);

export default AuthContext;
