import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import axios from 'axios';
import '../styles/BecomeSellerPage.css';

const BecomeSellerPage = () => {
  const { user, handleLogin } = useAuth();
  const navigate = useNavigate();
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [successMessage, setSuccessMessage] = useState('');
  const [formData, setFormData] = useState({
    businessName: '',
    businessAddress: '',
    phoneNumber: '',
    description: ''
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prevState => ({
      ...prevState,
      [name]: value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');
    setSuccessMessage('');

    try {
      // Validate form
      if (!formData.businessName) {
        throw new Error('Business name is required');
      }

      // Send request to backend
      const API_URL = 'http://localhost:5000/api';
      const response = await axios.post(
        `${API_URL}/user/become-seller`, 
        formData,
        {
          headers: {
            'Content-Type': 'application/json',
            'Authorization': `Bearer ${localStorage.getItem('token')}`
          }
        }
      );

      if (response.data.success) {
        // Update local storage with new token
        if (response.data.token) {
          localStorage.setItem('token', response.data.token);
          
          // Update user context
          await handleLogin(response.data.token);
        }
        
        setSuccessMessage('Your account has been upgraded to seller! Redirecting to seller dashboard...');
        
        // Redirect to seller dashboard after delay
        setTimeout(() => {
          navigate('/seller-dashboard');
        }, 2000);
      } else {
        throw new Error(response.data.message || 'Failed to upgrade account');
      }
    } catch (err) {
      console.error('Error becoming seller:', err);
      setError(err.response?.data?.message || err.message || 'An error occurred');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="become-seller-page">
      <div className="container">
        <h1>Become a Seller</h1>
        
        {user?.role === 'seller' ? (
          <div className="already-seller">
            <h2>You are already a seller!</h2>
            <p>You can manage your products in the Seller Dashboard</p>
            <button 
              className="primary-button"
              onClick={() => navigate('/seller-dashboard')}
            >
              Go to Seller Dashboard
            </button>
          </div>
        ) : (
          <>
            <div className="info-banner">
              <h2>Why Become a Seller?</h2>
              <div className="benefits">
                <div className="benefit">
                  <div className="icon">üí∞</div>
                  <h3>Extra Income</h3>
                  <p>Turn your pre-loved items into cash</p>
                </div>
                <div className="benefit">
                  <div className="icon">üå±</div>
                  <h3>Sustainability</h3>
                  <p>Give items a second life</p>
                </div>
                <div className="benefit">
                  <div className="icon">üåê</div>
                  <h3>Reach Customers</h3>
                  <p>Sell to our growing community</p>
                </div>
              </div>
            </div>
            
            {successMessage && (
              <div className="success-message">
                {successMessage}
              </div>
            )}
            
            {error && (
              <div className="error-message">
                {error}
              </div>
            )}
            
            <form onSubmit={handleSubmit} className="seller-form">
              <div className="form-group">
                <label htmlFor="businessName">Business/Store Name*</label>
                <input
                  type="text"
                  id="businessName"
                  name="businessName"
                  value={formData.businessName}
                  onChange={handleChange}
                  required
                />
              </div>
              
              <div className="form-group">
                <label htmlFor="businessAddress">Business Address</label>
                <input
                  type="text"
                  id="businessAddress"
                  name="businessAddress"
                  value={formData.businessAddress}
                  onChange={handleChange}
                />
              </div>
              
              <div className="form-group">
                <label htmlFor="phoneNumber">Phone Number</label>
                <input
                  type="tel"
                  id="phoneNumber"
                  name="phoneNumber"
                  value={formData.phoneNumber}
                  onChange={handleChange}
                />
              </div>
              
              <div className="form-group">
                <label htmlFor="description">Description</label>
                <textarea
                  id="description"
                  name="description"
                  value={formData.description}
                  onChange={handleChange}
                  rows="4"
                  placeholder="Tell buyers about yourself and what you sell..."
                ></textarea>
              </div>
              
              <button 
                type="submit" 
                className="submit-button"
                disabled={loading}
              >
                {loading ? 'Processing...' : 'Become a Seller'}
              </button>
            </form>
            
            <div className="seller-agreement">
              <p>By becoming a seller, you agree to our <a href="/terms">Terms and Conditions</a> and <a href="/policies">Seller Policies</a>.</p>
            </div>
          </>
        )}
      </div>
    </div>
  );
};

export default BecomeSellerPage;
